// 请配合Juno实现语句执行/回滚功能
func ExecuteOrder(c echo.Context) (err error) {
    u := new(executeStr)
    if err = c.Bind(u); err != nil {
        c.Logger().Error(err.Error())
        return
    }
    var order modal.CoreSqlOrder
    var sor modal.CoreDataSource
    var backup bool
    modal.DB().Where("work_id =?", u.WorkId).First(&order)
    modal.DB().Where("source =?", order.Source).First(&sor)

    if order.Backup == 1 {
        backup = true
    }

    ps := lib.Decrypt(sor.Password)

    s := ser.YerningAuditOrder{
        SQL:      order.SQL,
        Backup:   backup,
        Execute:  true,
        Source:   ser.Source{Addr: sor.IP, Port: sor.Port, User: sor.Username, Password: ps},
        WorkId:   order.WorkId,
        IsDML:    false,
        DataBase: order.DataBase,
        Table:    order.Table,
    }

    if order.Status != 2 && order.Status != 5 {
        c.Logger().Error("工单已执行过！操作不符合幂等性")
        return c.JSON(http.StatusOK, "工单已执行过！操作不符合幂等性")
    }

    if order.Type == 0 {
        go DDLExorder(c, &s, &order)
        modal.DB().Model(&modal.CoreSqlOrder{}).Where("work_id =?", order.WorkId).Updates(map[string]interface{}{"status": 3})
    }

    if order.Type == 1 {
        s.IsDML = true
        go DMLExorder(c, &s, &order, s.Source.DBPool)
        modal.DB().Model(&modal.CoreSqlOrder{}).Where("work_id =?", order.WorkId).Updates(map[string]interface{}{"status": 3})
    }
    return c.JSON(http.StatusOK, "工单已执行！")
}
